mapper.select:
select DISTINCT hi.AptName,hd.dong, hd.code, hd.dealAmount, hd.buildYear, hd.dealYear, hd.dealMonth, 
hd.dealDay, hd.area, hd.floor, hd.jibun, hd.type, hd.rentMoney, hi.lat, hi.lng 
FROM houseinfo as hi inner join housedeal as hd on hd.AptName=hi.AptName where hd.buildYear >= #{buildYear} and hd.dong = #{dong}

select DISTINCT hi.AptName,hd.dong, hd.code, hd.dealAmount, hd.buildYear, hd.dealYear, hd.dealMonth, 
hd.dealDay, hd.area, hd.floor, hd.jibun, hd.type, hd.rentMoney, hi.lat, hi.lng 
FROM houseinfo as hi inner join housedeal as hd on hd.AptName=hi.AptName where hd.AptName Like concat('%',#{AptName},'%') and hd.dong = #{dong}

select DISTINCT hi.AptName,hd.dong, hd.code, hd.dealAmount, hd.buildYear, hd.dealYear, hd.dealMonth, 
hd.dealDay, hd.area, hd.floor, hd.jibun, hd.type, hd.rentMoney, hi.lat, hi.lng 
FROM houseinfo as hi inner join housedeal as hd on hd.AptName=hi.AptName where hd.dong = #{dong}
